import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

export default class Navbar extends Component {

    constructor(props) {
        super(props);
    
        this.state = {
          leaderboardData: [],
          isLoading: false,
          error: null,
        };
      }
    
      componentDidMount() {
        this.fetchLeaderboard();
      }
    
      fetchLeaderboard = async () => {
        try {
          this.setState({ isLoading: true });
          const response = await axios.get('http://localhost:5000/leaderboard');
          this.setState({
            leaderboardData: response.data,
            isLoading: false,
            error: null,
          });
        } catch (error) {
          console.log(error);
          this.setState({ error: 'Unable to fetch leaderboard data' });
        }
      };  

    render() {
        const { leaderboardData, isLoading, error } = this.state;

        if (isLoading) {
          return <div>Loading...</div>;
        }
    
        if (error) {
          return <div>{error}</div>;
        }
    
        return (
            <nav className="navbar navbar-dark bg-dark navbar-expand-lg">
                <Link to="/" className="navbar-brand">HangmanGame</Link>
                <div className="collpase navbar-collapse">
                <ul className="navbar-nav mr-auto">
                    <li className="navbar-item">
                        <Link to="/home" className="nav-link">Homepage</Link>
                    </li>
                    <li className="navbar-item">
                        <Link to="/game" className="nav-link">Play Game</Link>
                    </li>
                    <li className="navbar-item">
                        <Link to="/search" className="nav-link">Search users</Link>
                    </li>
                    <li className="navbar-item">
                        <Link to="/" className="nav-link">Login</Link>
                    </li>
                    <li className="navbar-item">
                        <Link to="/signup" className="nav-link">Register</Link>
                    </li>
                    <li className="navbar-item">
                        <Link to="/" className="nav-link">Logout</Link>
                    </li>
                </ul>
                <h2>Leaderboard</h2>
                <table>
                    <thead>
                        <tr>
                            <th>Username</th>
                            <th>Wins</th>
                            <th>Total Points</th>
                        </tr>
                    </thead>
                    <tbody>
                        {leaderboardData.map((data, index) => (
                            <tr key={index}>
                                <td>{data.username}</td>
                                <td>{data.wins}</td>
                                <td>{data.totalpoints}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
 
                </div>
            </nav>
        );
    }
}
